import "../style/BoardPage.css";
import { Link } from "react-router-dom";

function BoardPage() {
  const boardList = [
    {
      id: 1,
      title: "[중요] 2023년 4월 19일 수어 영상 업데이트합니다.",
      writer: "정세연",
      date: "2022-04-19",
    },
    {
      id: 2,
      title: "[중요] 2023년 4월 12일 수어 영상 업데이트합니다.",
      writer: "권순규",
      date: "2022-04-12",
    },
    {
      id: 3,
      title: "[중요] 2023년 4월 03일 수어 영상 업데이트합니다.",
      writer: "민선홍",
      date: "2022-04-03",
    },
    {
      id: 4,
      title: "[중요] 2023년 3월 27일 수어 영상 업데이트합니다.",
      writer: "정세연",
      date: "2022-03-27",
    },
    {
      id: 5,
      title: "[중요] 2023년 3월 20일 수어 영상 업데이트합니다.",
      writer: "권순규",
      date: "2022-03-20",
    },
    {
      id: 6,
      title: "[중요] 2023년 3월 13일 수어 영상 업데이트합니다.",
      writer: "민선홍",
      date: "2022-03-13",
    },
  ];
  return (
    <div
      className="background"
      style={{
        backgroundImage: "url(./background.png)",
        backgroundSize: "cover",
        width: "100%",
        height: "auto",
        backgroundRepeat: "repeat-y",
      }}
    >
      <div className="BoardPage">
        <div className="BoardleftSide">
          <h1 id="BoardTitle">게시판</h1>
          <div className="BoardleftSideBtn">
            <Link to="/WriteBoard" id="WriteBoardBtn">
              글쓰기
            </Link>
          </div>
        </div>
        <div className="BoardrightSide"></div>
      </div>
    </div>
  );
}

export default BoardPage;
